package me.viserys.client;


import com.google.gson.*;
import me.viserys.client.impl.*;
import me.viserys.utils.Jsons;
import me.viserys.utils.Requests;

import java.util.ArrayList;
import java.util.List;

public class SelfClient extends Jsons {

    private final Requests requests;

    private static final Gson gson = new GsonBuilder().serializeNulls().create();

    public SelfClient(String _token) {
        requests = new Requests(_token);
    }

    public List<Friend> friends() {
        List<Friend> friends = new ArrayList<>();
        JsonArray data = requests.get("https://discord.com/api/v8/users/@me/relationships").getAsJsonArray();
        for (int i = 0; i < data.size(); i++) {
            JsonObject object = data.get(i).getAsJsonObject();
            String id = getString(object.get("id"));
            int type = getInt(object.get("type"));
            String nickname = getString(object.get("nickname"));
            JsonObject user = object.getAsJsonObject("user");
            String username = getString(user.get("username"));
            String displayName = getString(user.get("display_name"));
            String avatar = getString(user.get("avatar"));
            String avatarDecoration = getString(user.get("avatar_decoration"));
            String discriminator = getString(user.get("discriminator"));
            int publicFlags = getInt(user.get("public_flags"));
            Friend f = new Friend(id, type, nickname, username, displayName, avatar, avatarDecoration, discriminator, publicFlags);
            friends.add(f);
        }
        return friends;
    }

    public Token information() {
        JsonObject data = requests.get("https://discord.com/api/v9/users/@me").getAsJsonObject();
        String id = getString(data.get("id"));
        String username = getString(data.get("username"));
        String displayName = getString(data.get("display_name"));
        String avatar = getString(data.get("avatar"));
        String avatarDecoration = getString(data.get("avatar_decoration"));
        String discriminator = getString(data.get("discriminator"));
        int publicFlags = getInt(data.get("public_flags"));
        int flags = getInt(data.get("flags"));
        String banner = getString(data.get("banner"));
        String bannerColor = getString(data.get("banner_color"));
        int accentColor = getInt(data.get("accent_color"));
        String bio = getString(data.get("bio"));
        String locale = getString(data.get("locale"));
        boolean nsfw = getBoolean(data.get("nsfw_allowed"));
        boolean mfa = getBoolean(data.get("mfa_enabled"));
        int premiumType = getInt(data.get("premium_type"));
        String email = getString(data.get("email"));
        boolean verified = getBoolean(data.get("verified"));
        String phone = getString(data.get("phone"));
        return new Token(id, username, displayName, avatar, avatarDecoration, discriminator, publicFlags, flags,
                banner, bannerColor, accentColor, bio, locale, nsfw, mfa, premiumType, email, verified, phone);

    }


    public List<Guild> guilds() {
        List<Guild> guilds = new ArrayList<>();
        JsonArray data = requests.get("https://discord.com/api/v9/users/@me/guilds").getAsJsonArray();
        for (int i = 0; i < data.size(); i++) {
            JsonObject object = data.get(i).getAsJsonObject();
            String id = getString(object.get("id"));
            String name = getString(object.get("name"));
            String icon = getString(object.get("icon"));
            boolean owner = getBoolean(object.get("owner"));
            String permissions = getString(object.get("permissions"));
            Guild guild = new Guild(id, name, icon, owner, permissions);
            guilds.add(guild);
        }
        return guilds;
    }

    public List<Channel> channels() {
        List<Channel> channels = new ArrayList<>();
        JsonArray data = requests.get("https://discord.com/api/v8/users/@me/channels").getAsJsonArray();
        for (int i = 0; i < data.size(); i++) {
            JsonObject object = data.get(i).getAsJsonObject();
            String id = getString(object.get("id"));
            int type = getInt(object.get("type"));
            String lastMessageId = getString(object.get("last_message_id"));
            int flags = getInt(object.get("flags"));
            Channel channel = new Channel(id, type, lastMessageId, flags);
            channels.add(channel);
        }
        return channels;
    }

    public Channel channel(String channelId) {
        JsonObject data = requests.get("https://discord.com/api/v9/channels/" + channelId).getAsJsonObject();
        String id = getString(data.get("id"));
        int type = getInt(data.get("type"));
        String lastMessageId = getString(data.get("last_message_id"));
        int flags = getInt(data.get("flags"));
        return new Channel(id, type, lastMessageId, flags);
    }

    public List<Message> messages(String channelId) {
        List<Message> messages = new ArrayList<>();
        JsonArray data = requests.get("https://discord.com/api/v9/channels/" + channelId + "/messages").getAsJsonArray();
        for (int i = 0; i < data.size(); i++) {
            JsonObject object = data.get(i).getAsJsonObject();
            String id = getString(object.get("id"));
            int type = getInt(object.get("type"));
            String content = getString(object.get("content"));
            Message message = new Message(id, type, content);
            messages.add(message);
        }
        return messages;
    }

    public void send_message(String message, String channelId) {
        JsonObject json = new JsonObject();
        json.addProperty("content", message);
        requests.post("https://discord.com/api/v9/channels/" + channelId + "/messages", gson.toJson(json));
        System.out.println("message sent: [CHANNEL_ID:" + channelId + ", CONTENT:" + message + "]");
    }

    public void leave_guild(String guildId) {
        JsonObject json = new JsonObject();
        json.addProperty("lurking", false);
        requests.delete("https://discord.com/api/v9/users/@me/guilds/" + guildId, gson.toJson(json));
        System.out.println("leave guild: [GUILD_ID:" + guildId + "]");
    }

    public void join_guild(String inviteCode) {
        requests.post("https://discord.com/api/v9/invites/" + inviteCode, "{}");
        System.out.println("join guild: [INVITE_CODE:" + inviteCode + "]");
    }

    public void delete_guild(String guildId) {
        requests.post("https://discord.com/api/v9/guilds/" + guildId + "/delete", "{}");
        System.out.println("delete guild: [GUILD_ID:" + guildId + "]");
    }

    public void create_guild(String name) {
        JsonObject json = new JsonObject();
        json.addProperty("name", name);
        requests.post("https://discord.com/api/v9/guilds", gson.toJson(json));
        System.out.println("create guild: [NAME:" + name + "]");
    }

    public void add_friend(String name, String discriminator) {
        JsonObject json = new JsonObject();
        json.addProperty("username", name);
        json.addProperty("discriminator", discriminator);
        requests.post("https://discord.com/api/v9/users/@me/relationships", gson.toJson(json));
        System.out.println("add friend: [NAME:" + name + ", DISCRIMINATOR:" + discriminator + "]");
    }

    public void delete_friend(String id) {
        requests.delete("https://discord.com/api/v9/users/@me/relationships/" + id, "{}");
        System.out.println("remove friend: [ID:" + id + "]");
    }

    public void block_friend(String id) {
        JsonObject json = new JsonObject();
        json.addProperty("type", 2);
        requests.put("https://discord.com/api/v9/users/@me/relationships/" + id, gson.toJson(json));
        System.out.println("block friend: [ID:" + id + "]");
    }

    public void reset_avatar() {
        Avatar avatar = new Avatar(null);
        System.out.println(gson.toJson(avatar));
        requests.patch("https://discord.com/api/v9/users/@me", gson.toJson(avatar));
        System.out.println("reset avatar");
    }

    public void change_avatar() {
        Avatar avatar = new Avatar("data:image/png;base64,");
        requests.patch("https://discord.com/api/v9/users/@me", gson.toJson(avatar));
        System.out.println("change avatar");

    }

    public void delete_messages(String channelId, String messageId) {
        requests.delete("https://discord.com/api/v9/channels/" + channelId + "/messages/" + messageId, "{}");
        System.out.println("message delete: [CHANNEL_ID:" + channelId + ", MESSAGE_ID:" + messageId + "]");
    }


    public void close_dms(String channelId) {
        requests.delete("https://discord.com/api/v8/channels/" + channelId, "{}");
        System.out.println("close dms: [CHANNEL_ID:" + channelId + "]");
    }

    public void change_status(String text) {
        JsonObject json = new JsonObject();
        JsonObject custom_status = new JsonObject();
        custom_status.addProperty("text", text);
        json.add("custom_status", custom_status);
        requests.patch("https://discord.com/api/v8/users/@me/settings", gson.toJson(json));
        System.out.println("status: [TEXT:" + text + "]");
    }

    public void change_bio(String text) {
        JsonObject json = new JsonObject();
        json.addProperty("bio", text);
        requests.patch("https://discord.com/api/v9/users/@me/profile", gson.toJson(json));
        System.out.println("bio: [TEXT:" + text + "]");
    }

    public void change_theme(Theme theme) {
        JsonObject json = new JsonObject();
        switch (theme) {
            case DARK:
                json.addProperty("theme", "dark");
                break;
            case LIGHT:
                json.addProperty("theme", "light");
                break;
        }
        requests.patch("https://discord.com/api/v8/users/@me/settings", gson.toJson(json));
        System.out.println("theme: [THEME:" + theme + "]");
    }

    public void change_locale(Locale locale) {
        JsonObject json = new JsonObject();
        switch (locale) {
            case JAPAN:
            case THAI:
            case GREEK:
            case RUSSIAN:
            case BULGARIAN:
                json.addProperty("locale", locale.iso());
                break;
        }
        requests.patch("https://discord.com/api/v8/users/@me/settings", gson.toJson(json));
        System.out.println("locale: [LOCALE:" + locale.iso() + "]");
    }




    public enum Theme {
        LIGHT, DARK;
    }

    public enum Locale {
        GREEK("el"),
        JAPAN("ja"),
        BULGARIAN("bg"),
        RUSSIAN("ru"),
        THAI("th");
        private String iso;

        Locale(String _iso) {
            iso = _iso;
        }

        public String iso() {
            return iso;
        }
    }


}
